# Función para dividir líneas

def dividir_linea(linea):
    operadores = ['(', ')', '*', '+', '-', '/']
    partes = []
    palabra = ''

    for caracter in linea:
        if caracter.isalnum() or caracter == '_':
            palabra += caracter
        elif caracter in operadores:
            if palabra:
                partes.append(palabra)
                palabra = ''
            partes.append(caracter)
        else:
            if palabra:
                partes.append(palabra)
                palabra = ''

    if palabra:
        partes.append(palabra)

    return partes

